// Le type char
public class e04_type_char {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c = 60, ce = 'e', cg = 'g' ;
		byte b = 10 ;
		System.out.println((c + 1)) ;   // int
		System.out.println((2 * c)) ;   // int
		System.out.println((cg - ce)) ;   // int  // Unicode
		System.out.println((b * c)) ;     // byte en int

	}

}
