%%% Script for the first case:%%%  f(r) = (1-r^4)clear all;close all;clfclcUh0 = zeros(1,5); %% les valeurs de l'approximation en r=0Uex0 = zeros(1,5); %% le valeurs de la soln. exacte en r=0for m = 1:5  xi = linspace(-1,1,m+1);  xr = linspace(0,1,m+1);  r = linspace(0,1,100);  xx = linspace(-1,1,100);  Uh = zeros(1,m+1);  %%% Construir les matrices :  A = Ak (m);  B = Bk (m);  %%% conditions aux limites :  B(m+1) = 0;  for i = 1:m+1    if i==m+1      A(m+1,i) = 1;    else      A(m+1,i) = 0;    end  end  %%% Trouver la solution approchee:  Uh = transpose(A\B);  %%% Filling out the approximate solution (for a more continuous look)  ph = polyfit(xr,Uh,m);  Uhh = polyval(ph,r);  Uh0(m) = Uh(1);  %%% La solution exacte:  Uex = zeros(1,length(xi));  for i = 1:length(r)      Uex(i) = (+r(i)**2/4-r(i)**6/36-2/9);  end     Uex0(m) = Uex(1);  %%% Plotting solutions:  figure(m)  hold on;  plot(xr,Uh,'r .','markersize',10)  plot(r,Uhh,'r --')  plot(r,Uex,'b')  title("Premier Cas - Second Membre (1-r^4)",'fontsize',12)  xlabel("r",'fontsize',12)  ylabel("U(r) & Uh(r)",'fontsize',12)  h=legend("Uh*","Uh-smooth","U-exact",'location','southeast')  set(h,"fontsize",12)   end    %% Error Calculationxerr = zeros(1,m);Err = zeros(1,m);for i = 1:m  Err(i) = abs(Uh0(i)-Uex0(i))*100/abs(Uex0(i));  xerr(i) = i;endfigure(6)plot(xerr,Err,'r .-','markersize',10)title("Precision en r=0 - Premier Cas",'fontsize',12)xlabel("Degre du polynome (m)",'fontsize',12)ylabel("Precision (%)",'fontsize',12)