function B = B2k (m)  B = zeros(m+1,1);  [x,N,dN] = forme (m);  G = zeros(1,length(x));  f0 = 94;  r0 = 0.470  %Defining Heaviside function (not built into my version of Octave. grrr)   heaviside = zeros(1,length(x));  for i = 1:length(x)      end     for i = 1:m+1    for q = 1:length(x)      ri = (x(q)+1)/2; %define heaviside function        if abs(ri-r0)<=0.5          heaviside = 1;        else          heaviside = 0;        end %integrate         G(q) = -ri*N(i,q)*(f0*heaviside)/2;    end    B(i) = trapz(x,G);  end   endfunction  