%%% Script for the first case:%%%  f(r) = (1-r^4)clear all;close all;clfclc%%% Choose Polynomial degree for the approximate solution:m = 5;xi = linspace(-1,1,m+1);xr = linspace(0,1,m+1);r = linspace(0,1,100);xx = linspace(-1,1,100);Uh = zeros(1,m+1);%%% Construir les matrices :A = Ak (m);B = Bk (m);%%% conditions aux limites :B(m+1) = 0;for i = 1:m+1  if i==m+1    A(m+1,i) = 1;  else    A(m+1,i) = 0;  endend%%% Trouver la solution approchee:Uh = transpose(A\B);%%% Filling out the approximate solution (for a more continuous look)ph = polyfit(xr,Uh,m);Uhh = polyval(ph,r);%%% La solution exacte:Uex = zeros(1,length(xi));for i = 1:length(r)    Uex(i) = (+r(i)**2/4-r(i)**6/36-2/9);end %%% Plotting solutions:hold on;plot(xr,Uh,'r .','markersize',10)plot(r,Uhh,'r --')plot(r,Uex,'b')title("Solutions Exacte et Approchees")xlabel("r")ylabel("U(r) & Uh(r)")legend("Uh*","Uh-smooth","U-exact",'location','southeast')set(gca,"fontsize",12)